mapper.select:
SELECT COUNT(*)
  FROM myboard.board
 WHERE BOARD_DEL_AT = 'N';

mapper.insert:
INSERT INTO myboard.board
VALUES (NULL, #{boardTitle}, #{boardWriter}, #{boardContent}, sysdate(), 0, 'N');

    SELECT LAST_INSERT_ID();

mapper.update:
UPDATE myboard.board
   SET BOARD_DEL_AT = 'Y'
 WHERE BOARD_IDX = #{boardIdx}
